$("#metinGir").on("click",function(){
	if($(this).hasClass("selectedChoise") == false){
		$("#alanMetin").slideToggle("slow");
		$("#alanDosya").slideToggle("slow");
		$("#dosyaYukle").removeClass("selectedChoise");
		$(this).addClass("selectedChoise");
	}
});

$("#dosyaYukle").on("click",function(){
	if($(this).hasClass("selectedChoise") == false){
		$("#alanMetin").slideToggle("slow");
		$("#alanDosya").slideToggle("slow");
		$("#metinGir").removeClass("selectedChoise");
		$(this).addClass("selectedChoise");
	}
});
function kelimeHesapla(text){
	var toplam = text.split(/[\s,.]+/);
	console.log(toplam.length);
}

$("#metinGiris").on("keyup",function(){
		kelimeHesapla($(this).val());
});


